import React from 'react'
import { useState } from 'react'
import { FaRegTrashCan } from "react-icons/fa6";
const Face = () => {
  const [items,setItems]=useState(
    [
      {
        id:1,
        checked:false,
        name:"Reserve the Bus Ticket"
      },
      {
        id:2,
        checked:false,
        name:"Cancel the Bus Ticket"
      },
      {
        id:3,
        checked:false,
        name:"Date of the Bus"
      },
      {
        id:4,
        checked:false,
        name:"Timings Of the Bus"
      }
    ]
  )
  const handlechange=(a)=>{
     const ite=items.map((item)=>
     item.id===a?{...item,checked:!item.checked}:item)
     setItems(ite)
  }
  return (
    
    <div>
    <center><p>To Do List</p> </center>
      <ol>
        {
          items.map((item)=>(
            <li key={item.id}>
              <input
              type="checkbox"
              onChange={()=>handlechange(item.id)}
              checked={item.checked}/>
              <label>{item.name}</label>
              <FaRegTrashCan 
              role="button"
              onClick
              tabIndex={0}
              />
            </li>
          ))
        }
      </ol>
      
    </div>
  
  )
}

export default Face
