import './App.css';
import Header from './Header';
import Schedule from './Schedule';
import { useState } from 'react';
import Face from './Face';
import Footer from './Footer';
import Search from './Search';
import Render from './Render'
function App() {

  const [newItem, setNewItem] = useState('');
  const default1=JSON.parse(localStorage.getItem('todo'));
  const set= default1 ? default1 :[];
  const [items, setItems] = useState( set ); 
  const [search,setSearch] = useState('')


  const onSubmit = (e) => {
    e.preventDefault();
    add1(newItem);
    setNewItem('');
  };

  const add1 = (name) => {
    const id = items.length ? items[items.length - 1].id + 1 : 1;
    const add = { id, checked: false, name };
    const new1 = [...items, add];
    setItems(new1);
   localStorage.setItem('todo',JSON.stringify(new1));
  };

  const handlechange = (a) => {
    const ite = items.map((item) =>
      item.id === a ? { ...item, checked: !item.checked } : item
    );
    setItems(ite);
    localStorage.setItem('todo',JSON.stringify(ite));
  };

  const del = (a) => {
    const ite = items.filter((item) => item.id !== a);
    setItems(ite);
    localStorage.setItem('todo',JSON.stringify(ite))
  };

  return (
    <div>
      <Header title="Ticket Booking" />
      <Schedule />

      <Face
        handlechange={handlechange}
        del={del}
        items={items.filter(item => ((item.name).toLowerCase()).includes(search.toLowerCase()))}
        newItem={newItem}
        setNewItem={setNewItem}
        onSubmit={onSubmit}
      />
      <Search
      search = {search}
      setSearch = {setSearch}
      />
      <Footer length={items.length} />
      <Render/>
    </div>
  );
}

export default App;
