import './App.css';
import Input from './Input';
import Add from './Add';
import { useState } from 'react';

function App() {
  const [text, setText] = useState('');
  const [list, setList] = useState([]);
  const handle = (e) => {
    setText(e.target.value);
  }

  const add = () => {
    setList((prev) => [...prev, text]);
    setText('');
  }

  const del = (index) => {
    setList((prev) => prev.filter((prevtodo, prevtodoindex) => { return prevtodoindex !== index }))
}
  return (
    <div>
      <h1 className="Head">To Do List</h1>

      <Input text={text} handle={handle} add={add} />

      <Add list={list} del={del} />
    </div>
  );
}

export default App;
