import './App.css';
import Header from './Header';
import Schedule from './Schedule';
import { useState } from 'react';
import Face from './Face';
import Footer from './Footer';
function App() {

  const [newItem,setNewItem]=useState('')
  const onSubmit = (e) =>{
     e.preventDefault()
    add1(newItem)
     setNewItem('')

  }
  const add1 = (name) =>
  {
      const id=items.length? items[items.length-1].id + 1 :1
      const add={id,checked:false,name}
      const new1 =[...items,add]
      setItems(new1)

  }
  const default1=localStorage.getItem('todo_list');
  const initial=default1 ?  default1 : []
  console.log(default1);
  const [items,setItems]=useState(
    initial
  );

  const handlechange=(a)=>{
     const ite=items.map((item)=>
     (item.id===a?{...item,checked:!item.checked}:item))
     setItems(ite)
     localStorage.setItem('todo_list',JSON.stringify(ite))

  }
  const del=(a)=>
  {
    const ite=items.filter((item)=>item.id!==a)
    setItems(ite)
    localStorage.setItem('todo_list',JSON.stringify(ite))
  }

  return (
    <div>
      <Header title="Ticket Booking"/>
      <Schedule />
      <Face 
      handlechange={handlechange} 
      del={del} 
      items = {items}
      newItem={newItem} 
      setNewItem={setNewItem}
      onSubmit ={onSubmit}
       />
      <Footer length={items.length}/>
    </div>  
  );
}

export default App;
