import React from 'react';
import { FaRegTrashAlt } from 'react-icons/fa';

const Face = ({ handlechange, del, items }) => {
  return (
    <div>
      <center>
        <p>To Do List</p>
      </center>
      <form className='addItem' onSubmit={onSubmit}>
        <label htmlFor='addItem'>Add item</label>
        <br />
        <input
          id='add'
          type="text"
          placeholder='Add items'
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          required
        />
        <br />
        <button 
        type="submit"
        onClick={(e)=>onSubmit()}
        >
         add  <FaPlus />
        </button>
      </form>

      {items.length ? (
        <ol>
          {items.map((item) => (
            <li key={item.id}>
              <input
                type="checkbox"
                onChange={() => handlechange(item.id)}
                checked={item.checked}

              />
              <label id='check'>{item.name}</label>
              <FaRegTrashAlt
                role="button"
                onClick={() => del(item.id)}
                tabIndex={0}
              />
            </li>
          ))}
        </ol>
      ) : (
        "Your List is Empty"
      )}
    </div>
  );
};

export default Face;
