  const [list,setList] = useState([]);
  const handleChange = (e) =>
  {
    setText(e.target.value);
  }
  const Add = (e) =>
  {
    setList((prev) => [...prev,e.target.value]);
    setText('');
  }
  return (
    <div>
      <h1 className="Head">To Do List</h1>

       <Input text1={text1} handleChange={handleChange} a/>

       <Add list={list}/>
    </div>
  );
}

export default App;
