import React from 'react'
import { FaRegTrashCan } from "react-icons/fa6";
import { FaPlus } from 'react-icons/fa6';
const Face = ({handlechange,del,items,newItem,setNewItem,onSubmit}) => {
 
  return (
    
    <div>
    <center><p>To Do List</p> </center>
    <form className='addItem'>
      <label htmlFor='addItem'>Add item</label>
      <br></br>
      <input
      //autofocus
      id='add'
      type='text'
      placeholder='Add the items to list'
      value ={newItem}
      onChange={(e)=>setNewItem(e.target.value)}
      required
      ></input>
      <br></br>
      <button
     
      onClick = {(e)=>onSubmit(e)}
      >
       <FaPlus/>
      </button>
    </form>
    
   { (items.length)?
      (<ol>
          {
          items.map((item)=>(
            <li key={item.id}>
              <input
              type="checkbox"
              onChange={()=>handlechange(item.id)}
              checked={item.checked}
              />
              <label>{item.name}</label>
              <FaRegTrashCan 
              role="button"
              onClick={()=>del(item.id)}
              tabIndex={0}
              />
            </li>
          ))}
        
      </ol>):("Your List  is Empty")}
      
    </div>
  
  )
}

export default Face
