  {
    if(text1!=='')
    {
    setText(e.target.value);
    }
  }
  const add = () =>
  {
    setList((prev) => [...prev,text1]);
    set
  }
  return (
    <div>
      <h1 className="Head">To Do List</h1>

       <Input text1={text1} handleChange={handleChange} add={add}/>

       <Add list1={list}/>
    </div>
  );
}

