import './App.css';
import Header from './Header';
import Schedule from './Schedule';
import { useState } from 'react';
import Face from './Face';
import Footer from './Footer';
function App() {

  const [newItem,setNewItem]=useState('')
  const onSubmit = (e) =>{
     e.preventDefault()
    add1(newItem)
     setNewItem('')
  }
  const add1 = (name) =>
  {
      const id=items.length? items[items.length-1].id + 1 :1
      const add={id,checked:false,name}
      const new1 =[...items,add]
      setItems(new1)
  }
  const [items,setItems]=useState(
    [
      {
        id:1,
        checked:false,
        name:"Reserve the Bus Ticket"
      },
      {
        id:2,
        checked:false,
        name:"Cancel the Bus Ticket"
      },
      {
        id:3,
        checked:false,
        name:"Date of the Bus"
      },
      {
        id:4,
        checked:false,
        name:"Timings Of the Bus"
      }
    ]
  )

  const handlechange=(a)=>{
     const ite=items.map((item)=>
     item.id===a?{...item,checked:!item.checked}:item)
     setItems(ite)
  }
  const del=(a)=>
  {
    const ite=items.filter((item)=>item.id!==a)
    setItems(ite)
    localStorage.setItem
  }

  return (
    <div>
      <Header title="Ticket Booking"/>
      <Schedule />
      <Face 
      handlechange={handlechange} 
      del={del} 
      items = {items}
      newItem={newItem} 
      setNewItem={setNewItem}
      onSubmit ={onSubmit}
       />
      <Footer length={items.length}/>
    </div>  
  );
}

export default App;
