import React,{useState} from 'react'

const Handleinput = () => {
   
  return (
    <div>
      <input type='text' onChange={(e)=>setText(e.target.value)} value={text}></input>
      <h1>{text}</h1>
      <button onClick={()=>inc()}>+</button>
      <button onClick={()=>dec()}>-</button>
      <h2>The Count is {count}</h2>
    </div>
  )
}

export default Handleinput
