import React from 'react'

import { FaRegTrashCan } from "react-icons/fa6";
const Face = ({items,setItems}) => {
 
  const handlechange=(a)=>{
     const ite=items.map((item)=>
     item.id===a?{...item,checked:!item.checked}:item)
     setItems(ite)
  }
  const del=(a)=>
  {
    const ite=items.filter((item)=>item.id!==a)
    setItems(ite)
  }
  return (
    
    <div>
    <center><p>To Do List</p> </center>
      <ol>
        {
          items.map((item)=>(
            <li key={item.id}>
              <input
              type="checkbox"
              onChange={()=>handlechange(item.id)}
              checked={item.checked}/>
              <label>{item.name}</label>
              <FaRegTrashCan 
              role="button"
              onClick={()=>del(item.id)}
              tabIndex={0}
              />
            </li>
          ))
        }
      </ol>
      
    </div>
  
  )
}

export default Face
