import './App.css';
import Header from './Header';
import Schedule from './Schedule';
import Face from './Face';
import { useState } from 'react'
function App() {
  const [items,setItems]=useState(
    [
      {
        id:1,
        checked:false,
        name:"Reserve the Bus Ticket"
      },
      {
        id:2,
        checked:false,
        name:"Cancel the Bus Ticket"
      },
      {
        id:3,
        checked:false,
        name:"Date of the Bus"
      },
      {
        id:4,
        checked:false,
        name:"Timings Of the Bus"
      }
    ]
  )
  const handlechange=(a)=>{
    const ite=items.map((item)=>
    item.id===a?{...item,checked:!item.checked}:item)
    setItems(ite)
 }
 const del=(a)=>
 {
   const ite=items.filter((item)=>item.id!==a)
   setItems(ite)
 }

  return (
    <div>
      <Header title="Bus Ticket Booking"/>
      <Schedule />
      <Face 
      item={items}
      handlechange={handlechange}
      del
      />
    </div> 
  );
}

export default App;
