import './App.css';
import Input from './Input';
import Add from './Add';
import { useState } from 'react';

function App() {
  const [text1,setText] = useState('');
  const [list,setList] = useState([]);
  const handle = (e) =>
  {
    if(text1!=='')
    {
    setText(e.target.value);
    console.log(e.target.value);
    }
  }
  const add = () =>
  {
    setList((prev) => [...prev,text1]);
    setText('');
  }
  console.log(list)
  return (
    <div>
      <h1 className="Head">To Do List</h1>

       <Input text1={text1} handleChange={handle} add={add}/>

       <Add list={list}/>
    </div>
  );
}

export default App;
